&lt;b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;Основные характеристики:&lt;/span&gt;&lt;/b&gt;
&lt;hr&gt;
&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;Материал&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;: экокожа/хром;&lt;/span&gt;
&lt;hr&gt;
&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;Цвет&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;:&nbsp;&lt;/span&gt;&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt;черный;&nbsp;&lt;/span&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;бежевый;&nbsp;&lt;/span&gt;&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt;кремовый;&lt;/span&gt;&lt;br&gt;
&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;&lt;br&gt;
&lt;/span&gt;&lt;/b&gt;&lt;b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;Размер&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;font-size: 13pt; color: #000000;&quot;&gt;: 55*60*90;&lt;/span&gt;

&lt;b&gt;&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt;Основные характеристики:&lt;/span&gt;&lt;/b&gt;
&lt;hr&gt;
&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt; &lt;/span&gt;&lt;b&gt;&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt;Материал&lt;/span&gt;&lt;/b&gt;&lt;span style=&quot;color: #000000; font-size: 13pt;&quot;&gt;: чёрная сетка;&lt;/span&gt;
