	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Дуб белый Craft;&nbsp;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Дуб золотой Craft;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Дуб табачный Craft;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Белый;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Шпон натуральный;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Шпон тёмный;&lt;/span&gt;&lt;/li&gt;
	&lt;li style=&quot;color: #000000;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt;&quot;&gt;&nbsp;Шпон белёный;&lt;/span&gt;&lt;/li&gt;
