 &lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;/h3&gt;
 &lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt; color: #000000;&quot;&gt; &lt;/span&gt;
&lt;ul&gt;
&lt;/ul&gt;
 &lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt; color: #000000;&quot;&gt; &lt;/span&gt;&lt;span style=&quot;font-family: &amp;quot;Times New Roman&amp;quot;, Times; font-size: 14pt; color: #000000;&quot;&gt; &lt;/span&gt;
&lt;h3&gt;&lt;br&gt;
 &lt;/h3&gt;

&lt;p align=&quot;center&quot;&gt;
